import styled from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
    padding: 30px;
    background: #fff;
    border-radius: 4px;
    footer{
        display: flex;
        margin-top: 20px;
        justify-content: space-between;

        button{
            justify-content: space-between;
            display: flex;
            background: #d60202;
            border: 0px;
            border-radius: 5px;
            padding: 12px 20px;
            font-weight: bold;
            color: #eee;


            &:hover {
                background: ${darken(0.1,'#d60202')};
                transition: background 0.2s
            }

        }
    }
`;

export const ProductTable = styled.table`
    width: 100%;
    thead th{
        color: #999;
        text-align: left;
        padding: 12px;
    }
    tbody td{
        padding: 12px;
        vertical-align: middle;
        border-bottom: 1px solid #eee;
    }
    img{
        height: 100px;
    }
    strong{
        color: #333;
        display:block;
    }
    span{
        display: block;
        margin-top: 5px;
        font-size: 18px;
        font-weight: bold;
    }
    div{
        display: flex;
        align-items: center;

        input{
            border: 1px solid #ddd;
            border-radius: 4px;
            color: #666;
            padding: 4px;
            width: 50px;
        }
    }
    button{
        background: none;
        border: 0px;
        padding: 4px;
    }


`;

export const Total= styled.div`
    display:flex;
    align-items: baseline;
`;
