import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Container = styled.header`
    display:flex;
    justify-content: space-between;
    align-items: center;
    margin: 50px 0;
    background: #FFF;
    border-radius: 5px;
`;

export const Cart = styled(Link)`
    display: flex;
    align-items: center;
    margin-left: 10px;
`;
