import styled from 'styled-components';

export const ProductList = styled.ul `
    display: grid;
    grid-template-columns: repeat(3,1fr);
    list-style: none;
    grid-gap: 20px;
    li{
        display: flex;
        flex-direction: column;
        background: #fff;
        border-radius: 5px;
        padding: 20px;

        img{
            align-self: center;
            max-width: 230px
        }

        > strong{
            font-size: 16px;
            line-height: 20px;
            color: #333;
            margin-top:5px;
        }
        > span{
            font-size: 18px;
            font-weight: bold;
            margin: 10px 0 15px;
        }
    }    
`;
